mapper.insert:
INSERT INTO users (
    user_id, password, name, email, department, group_id, is_active, created_at, updated_at
) VALUES (
    #{userId}, #{password}, #{name}, #{email}, #{department}, #{groupId}, #{isActive}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE users SET
    last_login_at = #{lastLoginAt}
WHERE user_id = #{userId}
